import React, { useState, useEffect,useContext} from 'react';
import { CoffeeContext } from './CoffeeContext';
import classes from './MainPageOrder.module.css'; 
import axios from 'axios';

function FillMenu(props) {
    
    const componentsArray = Object.entries(props.components).map(([key, value]) => (
        <p key={key}>{key}: {value}</p>
    ));
    return (
        <div className={classes.box}>
        <div className={classes.content}>
            <h3>{props.name}</h3>
            {componentsArray}
            <span>{props.price}</span>
            <button className='btn' data-id={props.id} >Нажми</button>
        </div>
    </div>
    );
}
export default function Warehouse(){
    const { selectedCoffeePoint } = useContext(CoffeeContext); // Получаем выбранную кофейню из контекста
    const [productItems, setProductItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProductItems = async () => {
            try {
                const response = await axios.get('http://localhost:8080/batches', {
                    params: { coffeePointId: selectedCoffeePoint} // Используем ID выбранной кофейни
                });
                setProductItems(response.data);
                setLoading(false);
            } catch (error) {
                setError('Ошибка при загрузке данных о продуктах');
                setLoading(false);
            }
        };

        fetchProductItems();
    }, [selectedCoffeePoint]);

    return(
        <>
        <h1 className="heading">Склад</h1>
            <div className={classes.menu}>
                <div className={classes.box_container}>
                    {productItems.map((item, index) => (
                        <FillMenu key={index} {...item} />
                    ))}
                </div>
            </div>
        </>
    )
}
